{{ 
  (() => {
    // Ambil SKU yang dipilih dari Select1
    const selectedSku = Select1.selectedOptionValue; 

    // Cari item yang sesuai di FormBaruSelect1 berdasarkan SKU yang dipilih
    const selectedItem = FormBaruSelect1.data.find(row => row.meta_value === selectedSku);

    // Ambil harga per hari dari item yang dipilih, jika tidak ada, gunakan 0
    const pricePerDay = selectedItem && !isNaN(Number(selectedItem.price)) ? Number(selectedItem.price) : 0;

    // Jika Checkbox1 dicentang, gunakan nilai dari CurrencyInput1 sebagai harga
    if (Checkbox1.isChecked) {
      return Number((CurrencyInput1.text || CurrencyInput1.defaultValue || "0").replace(/[^\d]/g, ""));
    }

    // Ambil tanggal mulai dan akhir dari DatePicker1 dan DatePicker2
    const startDate = DatePicker1.selectedDate ? new Date(DatePicker1.selectedDate) : null;
    const endDate = DatePicker2.selectedDate ? new Date(DatePicker2.selectedDate) : null;

    // Jika salah satu tanggal tidak dipilih, gunakan harga untuk 1 hari sebagai default
    if (!startDate || !endDate) {
      return pricePerDay;
    }

    // Hitung jumlah hari
    let totalDays = 1; // Default minimal 1 hari
    if (startDate.toDateString() !== endDate.toDateString()) {
      // Konversi ke UTC agar tidak dipengaruhi zona waktu atau jam
      const startUTC = Date.UTC(startDate.getFullYear(), startDate.getMonth(), startDate.getDate());
      const endUTC = Date.UTC(endDate.getFullYear(), endDate.getMonth(), endDate.getDate());

      // Hitung selisih hari berbasis UTC dan tambahkan 1 agar inklusif
      totalDays = Math.round((endUTC - startUTC) / (1000 * 60 * 60 * 24)) + 1;
    }

    // Pastikan nilai yang dikembalikan tidak negatif
    totalDays = totalDays > 0 ? totalDays : 0;

    // Hitung total harga
    const totalPrice = totalDays * pricePerDay;

    return totalPrice;

  })() 
}}
