{{ 
(() => {
	// Ambil SKU yang dipilih dari Select1
	const selectedSku = Select1.selectedOptionValue; 
	// Cari item dalam data FormBaruSelect1 yang memiliki meta_value sesuai dengan SKU yang dipilih
	const selectedItem = FormBaruSelect1.data.find(row => row.meta_value === selectedSku);

	// Jika Checkbox1 dicentang, gunakan nilai dari CurrencyInput1, jika tidak ambil harga dari data
	if (Checkbox1.isChecked) {
		return Number(CurrencyInput1.text) || 0;
	}

	// Ambil tanggal mulai dari DatePicker1, jika tidak dipilih nilainya null
	const startDate = DatePicker1.selectedDate ? new Date(DatePicker1.selectedDate) : null;
	// Ambil tanggal akhir dari DatePicker2, jika tidak dipilih nilainya null
	const endDate = DatePicker2.selectedDate ? new Date(DatePicker2.selectedDate) : null;

	// Ambil harga dari data SKU
	const pricePerDay = Number(selectedItem?.price) || 0;

	// Jika tanggal mulai atau tanggal akhir tidak dipilih, gunakan harga untuk 1 hari sebagai default
	if (!startDate || !endDate) {
		return pricePerDay * 1; 
	}

	// Hitung selisih waktu dalam milidetik antara tanggal akhir dan tanggal mulai
	const diffTime = endDate - startDate;
	// Konversi selisih waktu menjadi jumlah hari, dibulatkan ke atas
	const totalDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 

	// Pastikan jumlah hari minimal 1 agar tidak ada kasus hasil 0 atau negatif
	const finalDays = Math.max(totalDays, 1);
	// Hitung total harga berdasarkan harga per hari dikalikan jumlah hari yang valid
	const totalPrice = pricePerDay * finalDays;

	return totalPrice; // Output angka murni tanpa format mata uang
})() 
}}
