{{ 
  (() => {
    const startDate = DatePicker1.selectedDate ? new Date(DatePicker1.selectedDate) : null;
    const endDate = DatePicker2.selectedDate ? new Date(DatePicker2.selectedDate) : null;

    // Ambil SKU yang dipilih dari Select1
    const selectedSku = Select1.selectedOptionValue; 
    const selectedItem = FormBaruSelect1.data.find(row => row.meta_value === selectedSku); // Cari item berdasarkan SKU

    // Ambil harga dari query tanpa mempertimbangkan checkbox
    const pricePerDay = Number(selectedItem?.price) || 0;

    if (!startDate || !endDate) {
      return pricePerDay * 1; // Default ke 1 hari jika tanggal kosong
    }


    // Hitung jumlah hari
    const diffTime = endDate - startDate;
    // Konversi selisih waktu menjadi jumlah hari, tambahkan 1 agar hari yang sama dihitung sebagai 1
    const totalDays = Math.floor(diffTime / (1000 * 60 * 60 * 24)) + 1;
    const finalDays = Math.max(totalDays, 1);
    const totalPrice = pricePerDay * finalDays

    return totalPrice; // Output angka murni tanpa format mata uang
  })() 
}}
